#!/usr/bin/env python3

sequence_a_and_sequence_b = open('./rosalind_data/rosalind_hamm.txt').read()
sequence_a, sequence_b = sequence_a_and_sequence_b.split()

hamming_distance = 0

# s = ''
# t = ''

for i in range(len(sequence_a)):
    if sequence_a[i] != sequence_b[i]:
        hamming_distance += 1

print(hamming_distance)